mapper.select:
select
  id, data_name, is_real, add_name, add_time, is_sparse, description, trans_count, item_count, avg_trans_len, max_trans_len, data_url
from dataset


        and id = #{id}


        and data_name = #{dataName}


        and is_real = #{isReal}


        and add_name = #{addName}


        and add_time = #{addTime}


        and is_sparse = #{isSparse}


        and description = #{description}


        and trans_count = #{transCount}


        and item_count = #{itemCount}


        and avg_trans_len = #{avgTransLen}


        and max_trans_len = #{maxTransLen}


        and data_url = #{dataUrl}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from dataset


        and id = #{id}


        and data_name = #{dataName}


        and is_real = #{isReal}


        and add_name = #{addName}


        and add_time = #{addTime}


        and is_sparse = #{isSparse}


        and description = #{description}


        and trans_count = #{transCount}


        and item_count = #{itemCount}


        and avg_trans_len = #{avgTransLen}


        and max_trans_len = #{maxTransLen}


        and data_url = #{dataUrl}
