mapper.select:
select
  id, user_id, user_name, sex, password, is_supper, creator, create_time, updater, update_time
from userEntity


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and sex = #{sex}


        and password = #{password}


        and is_supper = #{isSupper}


        and creator = #{creator}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from userEntity


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and sex = #{sex}


        and password = #{password}


        and is_supper = #{isSupper}


        and creator = #{creator}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}
