mapper.select:
select
id, user_id, user_name, sex, password, is_supper, creator, create_time, updater, update_time, address, tel,
email, dept, introduction, pfp_url, state
from user


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and sex = #{sex}


        and password = #{password}


        and is_supper = #{isSupper}


        and creator = #{creator}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and address = #{address}


        and tel = #{tel}


        and email = #{email}


        and dept = #{dept}


        and introduction = #{introduction}


        and pfp_url = #{pfpUrl}


        and state = #{state}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and sex = #{sex}


        and password = #{password}


        and is_supper = #{isSupper}


        and creator = #{creator}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and address = #{address}


        and tel = #{tel}


        and email = #{email}


        and dept = #{dept}


        and introduction = #{introduction}


        and pfp_url = #{pfpUrl}


        and state = #{state}

select count(1)
from user
where user_id = #{userId}
  and password = #{password}

mapper.update:
update user
set password=#{newPassword}
where user_id = #{userId}
  and password = #{password}
